import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [events, setEvents] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [description, setDescription] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [isAddingEvent, setIsAddingEvent] = useState(true);

  useEffect(() => {
    fetchEvents();
    fetchBlogs();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/events');
      setEvents(response.data);
    } catch (err) {
      setError('Failed to fetch events');
    }
  };

  const fetchBlogs = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/blogs');
      setBlogs(response.data);
    } catch (err) {
      setError('Failed to fetch blogs');
    }
  };

  const handleAddEvent = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    if (!title || !date || !description) {
      setError('Please fill all event fields');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/admin/events', { title, date, description });
      setSuccess('Event added successfully');
      setTitle('');
      setDate('');
      setDescription('');
      fetchEvents();
    } catch (err) {
      setError('Failed to add event');
    }
  };

  const handleAddBlog = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    if (!title || !content) {
      setError('Please fill all blog fields');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/admin/blogs', { title, content });
      setSuccess('Blog added successfully');
      setTitle('');
      setContent('');
      fetchBlogs();
    } catch (err) {
      setError('Failed to add blog');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    window.location.href = '/login';
  };

  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <h2 style={{ textAlign: 'center', marginBottom: '1.5rem' }}>Admin Dashboard</h2>
      <button onClick={handleLogout} style={{ float: 'right', marginBottom: '1rem', padding: '0.5rem 1rem', cursor: 'pointer' }}>
        Logout
      </button>
      <div style={{ marginBottom: '2rem' }}>
        <button onClick={() => setIsAddingEvent(true)} style={{ marginRight: '1rem', padding: '0.5rem 1rem', cursor: 'pointer' }}>
          Add Event
        </button>
        <button onClick={() => setIsAddingEvent(false)} style={{ padding: '0.5rem 1rem', cursor: 'pointer' }}>
          Add Blog
        </button>
      </div>
      {isAddingEvent ? (
        <form onSubmit={handleAddEvent} style={{ marginBottom: '2rem' }}>
          <h3>Add New Event</h3>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          {success && <p style={{ color: 'green' }}>{success}</p>}
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
          />
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
          />
          <textarea
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
          />
          <button type="submit" style={{ padding: '0.5rem 1rem', cursor: 'pointer' }}>
            Add Event
          </button>
        </form>
      ) : (
        <form onSubmit={handleAddBlog} style={{ marginBottom: '2rem' }}>
          <h3>Add New Blog</h3>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          {success && <p style={{ color: 'green' }}>{success}</p>}
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
          />
          <textarea
            placeholder="Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            style={{ width: '100%', padding: '0.5rem', marginBottom: '0.5rem' }}
          />
          <button type="submit" style={{ padding: '0.5rem 1rem', cursor: 'pointer' }}>
            Add Blog
          </button>
        </form>
      )}
      <h3>Existing Events</h3>
      <ul>
        {events.map((event) => (
          <li key={event.id} style={{ marginBottom: '1rem' }}>
            <strong>{event.title}</strong> - {event.date}
            <p>{event.description}</p>
          </li>
        ))}
      </ul>
      <h3>Existing Blogs</h3>
      <ul>
        {blogs.map((blog) => (
          <li key={blog.id} style={{ marginBottom: '1rem' }}>
            <strong>{blog.title}</strong>
            <p>{blog.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
