import React, { useEffect, useState } from 'react';

function Membership() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check if user info is passed in URL query (after OAuth redirect)
    const params = new URLSearchParams(window.location.search);
    const userParam = params.get('user');
    if (userParam) {
      try {
        const userObj = JSON.parse(userParam);
        setUser(userObj);
      } catch (error) {
        console.error('Failed to parse user info:', error);
      }
    }
  }, []);

  const handleLogin = () => {
    // Redirect to backend Google OAuth login
    window.location.href = 'http://localhost:5000/auth/google';
  };

  return (
    <section className="membership-section" data-aos="fade-up" style={{ padding: '2rem', maxWidth: '800px', margin: '0 auto' }}>
      <h2>Membership</h2>
      {!user ? (
        <>
          <p>Welcome to the Codeunia Membership page! Please log in with Google to become a member and access exclusive benefits.</p>
          <button onClick={handleLogin} style={{ padding: '0.5rem 1rem', fontSize: '1rem', cursor: 'pointer' }}>
            Login with Google
          </button>
        </>
      ) : (
        <>
          <p>Welcome, {user.name}!</p>
          <p>Your unique Member ID is: <strong>{user.member_id}</strong></p>
          <p>Enjoy access to exclusive workshops, hackathons, mentorship, and networking opportunities.</p>
          <button onClick={() => { window.location.href = 'http://localhost:5000/logout'; }} style={{ padding: '0.5rem 1rem', fontSize: '1rem', cursor: 'pointer' }}>
            Logout
          </button>
        </>
      )}
    </section>
  );
}

export default Membership;
